mapper.insert:
MERGE INTO T_ENV_MONI_RIV_HOURAUDIT AS T USING (VALUES
( ${item.pointCode}, '${item.polluteCode}', convert(dateTime, '${item.dataTime}',120), convert(dateTime, '${item.auditTime}',120), convert(dateTime, '${item.oriTime}',120), ${item.dataValue},null, ${item.dataType},null, convert(dateTime, '${item.updateTime}',120), '${item.dataFlag}',null, '${item.qn}',null, '${item.dataStatus}'null )
) AS S ( POINTCODE, POLLUTECODE, DATATIME, AUDITTIME, ORITIME, DATAVALUE, DATATYPE, UPDATETIME, DATAFLAG, qn, DATASTATUS) ON S.POINTCODE=T.POINTCODE AND S.POLLUTECODE=T.POLLUTECODE AND S.DATATIME=T.DATATIME AND S.DATATYPE=T.DATATYPE WHEN MATCHED THEN UPDATE SET T.AUDITTIME= S.AUDITTIME, T.ORITIME= S.ORITIME, T.DATAVALUE=S.DATAVALUE, T.UPDATETIME=S.UPDATETIME, T.DATAFLAG=S.DATAFLAG, T.qn=S.qn, T.DATASTATUS=S.DATASTATUS WHEN NOT MATCHED THEN INSERT ( POINTCODE, POLLUTECODE, DATATIME, AUDITTIME, ORITIME, DATAVALUE, DATATYPE, UPDATETIME, DATAFLAG, qn, DATASTATUS) VALUES ( POINTCODE, POLLUTECODE, DATATIME, AUDITTIME, ORITIME, DATAVALUE, DATATYPE, UPDATETIME, DATAFLAG, qn, DATASTATUS);

mapper.select:
select d.POINTCODE, d.POLLUTECODE, d.dataTime, d.dataValue, d.dataFlag from T_ENV_MONI_RIV_DATAHH d WHERE 1=1
AND d.pointCode=#{pointCode}
AND d.DATATIME >= #{beginTime}
AND d.DATATIME <= #{endTime}
AND d.POLLUTECODE IN
#{polluteCode}

select d.POINTCODE AS pointCode, d.POLLUTECODE AS ParamID, d.DATATIME AS dataTime, d.DATAVALUE AS StandardValue, d.DCHECK AS 'Check', d.VOLUME AS Volume, d.DVOLUME AS DVolume, d.DATAFLAG AS Flag from T_ENV_MONI_RIV_CHKDATA d WHERE DATATYPE = 0
AND d.pointCode=#{pointCode}
AND d.DATATIME >= #{beginTime}
AND d.DATATIME <= #{endTime}
AND d.POLLUTECODE IN
#{polluteCode}

select d.POINTCODE AS pointCode, d.POLLUTECODE AS ParamID, d.FDATATIME AS waterTime, d.DATATIME AS dataTime, d.DATAVALUE AS Water, d.DCHECK AS 'Check', d.DCHROMA AS Chroma, d.VOLUME AS Volume, d.DVOLUME AS DVolume, d.DATAFLAG AS Flag from T_ENV_MONI_RIV_RCYLDATA d WHERE DATATYPE = 0
AND d.pointCode=#{pointCode}
AND d.DATATIME >= #{beginTime}
AND d.DATATIME <= #{endTime}
AND d.POLLUTECODE IN
#{polluteCode}

select d.POINTCODE AS pointCode, d.POLLUTECODE AS ParamID, d.DATATIME AS dataTime, d.DATAVALUE AS StandardValue, d.SPANVALUE AS SpanValue, d.DCHECK AS 'Check', d.DATAFLAG AS Flag from T_ENV_MONI_RIV_DATAAUDIT d WHERE 1=1
AND d.dataType=#{dataType}
AND d.pointCode=#{pointCode}
AND d.DATATIME >= #{beginTime}
AND d.DATATIME <= #{endTime}
AND d.POLLUTECODE IN
#{polluteCode}

mapper.update:


UPDATE  T_ENV_MONI_RIV_DATAHH SET 
	DATAFLAG = #{QcFlag},
	ISVALIDED=0
WHERE pointCode=#{pointCode}

    AND  DATATIME  > #{beginTime}


	AND  DATATIME <= #{endTime}

   AND POLLUTECODE IN

	#{polluteCode}

mapper.select:
select d.POINTCODE AS pointCode, d.POLLUTECODE AS ParamID, d.DATATIME AS dataTime, d.DATAVALUE AS StandardValue
from T_ENV_MONI_RIV_MAUALMONTH d WHERE DATATYPE = 1
AND d.pointCode=#{pointCode}
AND d.DATATIME = #{dataTime}
AND d.POLLUTECODE IN
#{polluteCode}

select SUM(CASE WHEN 1=1
AND auditTime >= #{beginTime}
AND auditTime < #{endTime}
THEN 1 ELSE 0 END) as DATASUM, DATATYPE,POINTCODE, max(auditTime) as auditTime from T_ENV_MONI_RIV_DATAAUDIT where DATASTATUS = 1 and DATATYPE in (1,2) GROUP BY POINTCODE,DATATYPE
